import React, { Fragment, useContext, useEffect, useState } from "react";
import AllReviews from "./AllReviews";
import ReviewForm from "./ReviewForm";

import { ProductDetailsContext } from "./";
import { LayoutContext } from "../layout";

import { isAuthenticate } from "../auth/fetchApi";

import "./style.css";

const Menu = () => {
  const { data, dispatch } = useContext(ProductDetailsContext);
  const { data: layoutData } = useContext(LayoutContext);

  return (
    <div className="flex flex-col md:flex-row items-center justify-center">
      <div
        onClick={() => dispatch({ type: "menu", payload: true })}
        className={`$ {
          data.menu ? "border-b-2 border-yellow-700" : ""
        } px-4 py-3 cursor-pointer`}
      >
        Description
      </div>
      <div
        onClick={() => dispatch({ type: "menu", payload: false })}
        className={`$ {
          !data.menu ? "border-b-2 border-yellow-700" : ""
        } px-4 py-3 relative flex cursor-pointer`}
      >
        <span>Reviews</span>
        <span className="absolute text-xs top-0 right-0 mt-2 bg-yellow-700 text-white rounded px-1">
          {layoutData.singleProductDetail.pRatingsReviews.length}
        </span>
      </div>
    </div>
  );
};

const RatingReview = () => {
  return (
    <Fragment>
      <AllReviews />
      {isAuthenticate() ? (
        <ReviewForm />
      ) : (
        <div className="mb-12 md:mx-16 lg:mx-20 xl:mx-24 bg-red-200 px-4 py-2 rounded mb-4">
          You need to login to leave a review.
        </div>
      )}
    </Fragment>
  );
};

const ProductDetailsSectionTwo = () => {
  const { data } = useContext(ProductDetailsContext);
  const { data: layoutData } = useContext(LayoutContext);
  const [singleProduct, setSingleproduct] = useState({});

  useEffect(() => {
    setSingleproduct(
      layoutData.singleProductDetail ? layoutData.singleProductDetail : {}
    );
  }, [layoutData.singleProductDetail]);

  return (
    <Fragment>
      <section className="max-w-7xl mx-auto px-4 py-8">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
          {/* Product Image */}
          <div>
            <img
              src={`http://localhost:8000/uploads/products/${singleProduct.pImages?.[0]}`}
              alt={singleProduct.pName}
              className="w-full h-[480px] object-cover rounded-lg shadow-md"
            />
          </div>

          {/* Product Info */}
          <div>
            <h2 className="text-3xl font-bold text-gray-800 mb-2">
              {singleProduct.pName}
            </h2>
            <div className="flex items-center mb-3">
              <div className="flex text-yellow-500 text-sm">
                {[...Array(5)].map((_, i) => (
                  <span key={i}>★</span>
                ))}
              </div>
              <span className="text-gray-600 text-sm ml-2">
                ({singleProduct.pRatingsReviews?.length || 0} reviews)
              </span>
            </div>

            <p className="text-gray-700 text-sm mb-4">
              {singleProduct.pDescription}
            </p>

            <div className="flex items-center space-x-4 mb-4">
              <span className="text-2xl font-bold text-gray-900">
                ${singleProduct.pPrice}
              </span>
              {singleProduct.oldPrice && (
                <span className="line-through text-gray-500">
                  ${singleProduct.oldPrice}
                </span>
              )}
              {singleProduct.pOffer > 0 && (
                <span className="text-sm bg-red-600 text-white px-2 py-0.5 rounded">
                  {singleProduct.pOffer}% OFF
                </span>
              )}
            </div>

            {/* Bottle Sizes */}
            <div className="mb-4">
              <span className="text-gray-600 font-medium">Bottle Size:</span>
              <div className="flex mt-2 space-x-3">
                {["50ml", "100ml", "150ml"].map((size) => (
                  <button
                    key={size}
                    className="px-4 py-1 border border-gray-400 text-gray-700 rounded hover:bg-yellow-100"
                  >
                    {size}
                  </button>
                ))}
              </div>
            </div>

            {/* Add to Cart Section */}
            <div className="flex items-center space-x-3 mt-6">
              <input
                type="number"
                min="1"
                defaultValue="1"
                className="w-16 px-2 py-1 border rounded"
              />
              <button className="px-6 py-2 bg-yellow-600 hover:bg-yellow-700 text-white rounded">
                Add to Cart
              </button>
              <button className="text-yellow-600 hover:text-yellow-700">
                ♥ Wishlist
              </button>
            </div>
          </div>
        </div>

        {/* Description & Reviews */}
        <div className="mt-12">
          <Menu />
          {data.menu ? (
            <div className="mt-6 text-gray-700">
              {singleProduct.pDescription || "No description available."}
            </div>
          ) : (
            <RatingReview />
          )}
        </div>

        {/* Category Info */}
        <div className="mt-10 border-t pt-4 text-gray-700">
          <strong>Category:</strong> {singleProduct.pCategory?.cName || "-"}
        </div>
      </section>
    </Fragment>
  );
};

export default ProductDetailsSectionTwo;
