// src/utils/auth.js
import axios from "axios";

// ✅ Check if user is logged in (returns user object or false)
export const isAuthenticate = () =>
  localStorage.getItem("jwt")
    ? JSON.parse(localStorage.getItem("jwt"))
    : false;

// ✅ Login request for users only
export const loginReq = async ({ email, password }) => {
  const data = { email, password };
  try {
    const res = await axios.post(`http://localhost:8000/api/signin`, data);

    // Save token and user to localStorage
    if (res.data.token) {
      localStorage.setItem("jwt", JSON.stringify(res.data));
    }

    return res.data;
  } catch (error) {
    console.log("Login error:", error);
    return { error: "Login failed. Please try again." };
  }
};

// ✅ Signup request for users only (no admin role allowed)
export const signupReq = async ({ name, email, password, cPassword }) => {
  const data = { name, email, password, cPassword };
  try {
    const res = await axios.post(`http://localhost:8000/api/signup`, data);
    return res.data;
  } catch (error) {
    console.log("Signup error:", error);
    return { error: "Something went wrong while signing up." };
  }
};

// ✅ Logout function
export const logout = () => {
  localStorage.removeItem("jwt");
};
