import React, { useEffect, useState, useContext } from "react";
import { LayoutContext } from "../layout";

const RecommendedProducts = () => {
  const [products, setProducts] = useState([]);
  const { data: layoutData } = useContext(LayoutContext);

  useEffect(() => {
    const categoryId = layoutData.singleProductDetail?.pCategory?._id;
    if (categoryId) {
      fetch(`http://localhost:8000/api/product/category/${categoryId}`)
        .then((res) => res.json())
        .then((data) => {
          setProducts(data);
        })
        .catch((err) => console.error("Fetch error", err));
    }
  }, [layoutData.singleProductDetail]);

  return (
    <div className="my-8 px-4 md:px-12 animate-fade-in">
      <h2 className="text-xl md:text-2xl font-semibold mb-4 text-center text-gray-800">
        Recommended Products
      </h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
        {products.map((product) => (
          <div
            key={product._id}
            className="border rounded-lg overflow-hidden shadow hover:shadow-md transition-all duration-300 cursor-pointer"
          >
            <img
              src={`http://localhost:8000/uploads/products/${product.pImage}`}
              alt={product.pName}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h3 className="text-gray-800 font-medium truncate">{product.pName}</h3>
              <p className="text-yellow-700 font-semibold">${product.pPrice}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecommendedProducts;
