import React, { Fragment, useContext, useEffect, useState } from "react";
import AllReviews from "./AllReviews";
import ReviewForm from "./ReviewForm";

import { ProductDetailsContext } from "./";
import { LayoutContext } from "../layout";

import { isAuthenticate } from "../auth/fetchApi";

import "./style.css";

const Menu = () => {
  const { data, dispatch } = useContext(ProductDetailsContext);
  const { data: layoutData } = useContext(LayoutContext);

  return (
    <div className="flex justify-center space-x-6 border-b border-gray-200 py-4 animate-fade-in">
      <button
        onClick={() => dispatch({ type: "menu", payload: true })}
        className={`text-lg font-medium transition-all duration-300 ${
          data.menu ? "text-yellow-700 border-b-2 border-yellow-700" : "text-gray-600 hover:text-yellow-700"
        }`}
      >
        Description
      </button>
      <button
        onClick={() => dispatch({ type: "menu", payload: false })}
        className={`text-lg font-medium relative transition-all duration-300 ${
          !data.menu ? "text-yellow-700 border-b-2 border-yellow-700" : "text-gray-600 hover:text-yellow-700"
        }`}
      >
        Reviews
        <span className="absolute -top-3 -right-4 text-xs bg-yellow-700 text-white rounded-full px-2 py-0.5">
          {layoutData.singleProductDetail?.pRatingsReviews?.length || 0}
        </span>
      </button>
    </div>
  );
};

const RatingReview = () => {
  return (
    <Fragment>
      <AllReviews />
      {isAuthenticate() ? (
        <ReviewForm />
      ) : (
        <div className="my-4 mx-4 md:mx-16 bg-red-100 text-red-800 px-4 py-2 rounded shadow animate-fade-in">
          You need to login to submit a review.
        </div>
      )}
    </Fragment>
  );
};

const RecommendedProducts = () => {
  // This should be replaced with real product fetching logic
  const products = [
    { id: 1, name: "Classic Perfume", image: "1.jpg", price: "$45" },
    { id: 2, name: "Floral Mist", image: "2.jpg", price: "$60" },
    { id: 3, name: "Ocean Breeze", image: "3.jpg", price: "$75" },
    { id: 4, name: "Night Bloom", image: "4.jpg", price: "$55" },
  ];

  return (
    <div className="my-8 px-4 md:px-12 animate-fade-in">
      <h2 className="text-xl md:text-2xl font-semibold mb-4 text-center text-gray-800">
        Recommended Products
      </h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
        {products.map((product) => (
          <div
            key={product.id}
            className="border rounded-lg overflow-hidden shadow hover:shadow-md transition-all duration-300 cursor-pointer"
          >
            <img
              src={`http://localhost:8000/uploads/products/${product.image}`}
              alt={product.name}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h3 className="text-gray-800 font-medium truncate">{product.name}</h3>
              <p className="text-yellow-700 font-semibold">{product.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const ProductDetailsSectionTwo = () => {
  const { data } = useContext(ProductDetailsContext);
  const { data: layoutData } = useContext(LayoutContext);
  const [singleProduct, setSingleproduct] = useState({});

  useEffect(() => {
    setSingleproduct(layoutData.singleProductDetail || {});
  }, [layoutData.singleProductDetail]);

  return (
    <Fragment>
      <section className="m-4 md:mx-12 md:my-8 animate-fade-in">
        <Menu />
        {data.menu ? (
          <div className="mt-6 text-gray-700 leading-relaxed text-justify">
            {singleProduct.pDescription}
          </div>
        ) : (
          <RatingReview />
        )}
      </section>
      <div className="m-4 md:mx-8 md:my-6 flex justify-center capitalize font-light tracking-widest bg-white border-t border-b text-gray-800 px-4 py-4">
        <div>
          <span className="font-medium">Category :</span>
          <span className="text-sm text-gray-600 ml-2">
            {singleProduct.pCategory ? singleProduct.pCategory.cName : "N/A"}
          </span>
        </div>
      </div>
      <RecommendedProducts />
    </Fragment>
  );
};

export default ProductDetailsSectionTwo;
