import React, { Fragment, useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import { ProductDetailsContext } from "./index";
import { LayoutContext } from "../layout";
import Submenu from "./Submenu";
import ProductDetailsSectionTwo from "./ProductDetailsSectionTwo";

import { getSingleProduct } from "./FetchApi";
import { cartListProduct } from "../partials/FetchApi";
import { isWishReq, unWishReq, isWish } from "../home/Mixins";
import { updateQuantity, slideImage, addToCart, cartList } from "./Mixins";
import { totalCost } from "../partials/Mixins";

const ProductDetailsSection = () => {
  const { id } = useParams();
  const { data, dispatch } = useContext(ProductDetailsContext);
  const { data: layoutData, dispatch: layoutDispatch } = useContext(LayoutContext);

  const [count, setCount] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [selectedSize, setSelectedSize] = useState("100ml");
  const [alertq, setAlertq] = useState(false);
  const [pImages, setPimages] = useState([]);
  const [wList, setWlist] = useState(JSON.parse(localStorage.getItem("wishList")));

  const sProduct = layoutData.singleProductDetail;

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    dispatch({ type: "loading", payload: true });
    try {
      const responseData = await getSingleProduct(id);
      if (responseData?.Product) {
        layoutDispatch({ type: "singleProductDetail", payload: responseData.Product });
        setPimages(responseData.Product.pImages);
        layoutDispatch({ type: "inCart", payload: cartList() });
      }
      dispatch({ type: "loading", payload: false });
    } catch (err) {
      console.log(err);
    }
    fetchCartProduct();
  };

  const fetchCartProduct = async () => {
    try {
      const res = await cartListProduct();
      if (res?.Products) layoutDispatch({ type: "cartProduct", payload: res.Products });
    } catch (err) {
      console.log(err);
    }
  };

  if (data.loading)
    return <div className="flex justify-center items-center h-screen text-xl">Loading...</div>;
  if (!sProduct)
    return <div className="text-center text-gray-500 mt-10">Product not found.</div>;

  return (
    <Fragment>
      {/* Breadcrumb Navigation */}
      <div className="mx-4 md:mx-12 mt-6">
        <Submenu
          value={{
            categoryId: sProduct.pCategory._id,
            product: sProduct.pName,
            category: sProduct.pCategory.cName,
          }}
        />
      </div>

      {/* Product Section */}
      <section className="mx-4 md:mx-12 my-10">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-start">
          {/* Product Images */}
          <div className="space-y-4">
            <div className="relative border rounded-xl overflow-hidden shadow bg-white">
              <img
                src={`http://localhost:8000/uploads/products/${pImages[count]}`}
                alt="product"
                className="w-full h-[500px] object-contain"
              />
              <div className="absolute inset-0 flex justify-between items-center px-4">
                <button
                  onClick={() => slideImage("decrease", null, count, setCount, pImages)}
                  className="text-3xl text-gray-400 hover:text-yellow-600"
                >
                  ◀
                </button>
                <button
                  onClick={() => slideImage("increase", null, count, setCount, pImages)}
                  className="text-3xl text-gray-400 hover:text-yellow-600"
                >
                  ▶
                </button>
              </div>
            </div>

            <div className="flex space-x-3 overflow-x-auto">
              {pImages.map((img, index) => (
                <img
                  key={index}
                  onClick={() => setCount(index)}
                  src={`http://localhost:8000/uploads/products/${img}`}
                  alt="thumb"
                  className={`w-20 h-20 object-cover border-2 rounded-md cursor-pointer transition-transform ${
                    count === index
                      ? "border-yellow-600 ring-2 ring-yellow-400 scale-105"
                      : "border-gray-200"
                  }`}
                />
              ))}
            </div>
          </div>

          {/* Product Details */}
          <div className="space-y-5 px-2">
            <h1 className="text-4xl font-bold text-gray-800">{sProduct.pName}</h1>

            <div className="flex items-center space-x-2 text-yellow-600">
              <span className="text-xl">★★★★☆</span>
              <span className="text-sm text-gray-500">
                ({sProduct.pRatingsReviews?.length || 0} reviews)
              </span>
            </div>

            <p className="text-gray-600 leading-relaxed text-md">{sProduct.pDescription}</p>

            <div className="text-3xl font-bold space-x-3">
              {sProduct.discount && (
                <span className="line-through text-gray-400 text-lg">
                  ${sProduct.pPrice + 20}.00
                </span>
              )}
              <span className="text-yellow-700">${sProduct.pPrice}.00</span>
            </div>

            {/* Bottle Size Selector */}
            <div>
              <h3 className="text-sm font-medium mb-1 text-gray-700">Bottle Size</h3>
              <div className="flex space-x-3">
                {["50ml", "100ml", "150ml"].map((size) => (
                  <button
                    key={size}
                    onClick={() => setSelectedSize(size)}
                    className={`px-4 py-1 text-sm rounded-full transition ${
                      selectedSize === size
                        ? "bg-yellow-600 text-white"
                        : "border border-gray-300 text-gray-700"
                    }`}
                  >
                    {size}
                  </button>
                ))}
              </div>
            </div>

            {/* Quantity + Cart + Wishlist */}
            <div className="flex flex-wrap items-center gap-4">
              <div className="flex items-center border rounded px-3 py-1">
                <button
                  onClick={() =>
                    updateQuantity("decrease", sProduct.pQuantity, quantity, setQuantity, setAlertq)
                  }
                  className="text-xl text-gray-700"
                >
                  −
                </button>
                <span className="mx-4">{quantity}</span>
                <button
                  onClick={() =>
                    updateQuantity("increase", sProduct.pQuantity, quantity, setQuantity, setAlertq)
                  }
                  className="text-xl text-gray-700"
                >
                  +
                </button>
              </div>

              {layoutData.inCart?.includes(sProduct._id) ? (
                <button
                  disabled
                  className="bg-gray-400 text-white px-6 py-2 rounded uppercase cursor-not-allowed"
                >
                  In Cart
                </button>
              ) : (
                <button
                  onClick={() =>
                    addToCart(
                      sProduct._id,
                      quantity,
                      sProduct.pPrice,
                      layoutDispatch,
                      setQuantity,
                      setAlertq,
                      fetchData,
                      totalCost
                    )
                  }
                  className="bg-yellow-600 hover:bg-yellow-700 text-white px-6 py-2 rounded uppercase"
                >
                  Add to Cart
                </button>
              )}

              <button
                onClick={() =>
                  isWish(sProduct._id, wList)
                    ? unWishReq(null, sProduct._id, setWlist)
                    : isWishReq(null, sProduct._id, setWlist)
                }
                className="border border-yellow-600 text-yellow-600 px-4 py-2 rounded hover:bg-yellow-50 transition"
              >
                {isWish(sProduct._id, wList) ? "Remove Wishlist" : "Add to Wishlist"}
              </button>
            </div>

            {+quantity === +sProduct.pQuantity && (
              <div className="text-sm text-red-500 mt-1">
                Only {sProduct.pQuantity} left in stock!
              </div>
            )}
          </div>
        </div>
      </section>

      <ProductDetailsSectionTwo />
    </Fragment>
  );
};

export default ProductDetailsSection;
