import React, { Fragment, useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { getAllProduct } from "./fetchApi";
import { HomeContext } from "./index";
import { isWishReq, unWishReq, isWish } from "./";

const Shop = () => {
  const { data, dispatch } = useContext(HomeContext);
  const { products } = data;
  const navigate = useNavigate();
  const [wList, setWlist] = useState(JSON.parse(localStorage.getItem("wishList")) || []);

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line
  }, []);

  const fetchData = async () => {
    dispatch({ type: "loading", payload: true });
    try {
      const responseData = await getAllProduct();
      if (responseData && responseData.Products) {
        dispatch({ type: "setProducts", payload: responseData.Products });
      }
      dispatch({ type: "loading", payload: false });
    } catch (err) {
      console.log(err);
    }
  };

  if (data.loading) {
    return (
      <div className="flex items-center justify-center py-32">
        <svg className="w-12 h-12 animate-spin text-yellow-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8 8 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8 8 0 01-15.357-2m15.357 2H15" />
        </svg>
      </div>
    );
  }

  return (
    <Fragment>
      <div className="container mx-auto px-4 py-10">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">Shop Products</h1>
        {products && products.length > 0 ? (
          <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
            {products.map((item, index) => (
              <div
                key={index}
                className="bg-white rounded-lg shadow-md overflow-hidden relative hover:shadow-xl transition duration-300 transform hover:scale-105"
              >
                <img
                  onClick={() => navigate(`/products/${item._id}`)}
                  src={`http://localhost:8000/uploads/products/${item.pImages[0]}`}
                  alt={item.pName}
                  className="w-full h-56 object-cover cursor-pointer transition-all duration-500 ease-in-out"
                />
                <div className="p-4">
                  <div className="flex justify-between items-center mb-2">
                    <h2 className="text-md font-medium text-gray-700 truncate">{item.pName}</h2>
                    <div className="flex items-center space-x-1">
                      <svg
                        className="w-4 h-4 text-yellow-600"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118L4.96 10.1c-.784-.57-.38-1.81.588-1.81h4.915a1 1 0 00.951-.69l1.519-4.674z"
                        />
                      </svg>
                      <span className="text-sm text-gray-600">{item.pRatingsReviews.length}</span>
                    </div>
                  </div>
                  <div className="text-lg font-bold text-gray-900">${item.pPrice}.00</div>
                </div>

                {/* Wishlist Icons */}
                <div className="absolute top-2 right-2">
                  {!isWish(item._id, wList) ? (
                    <svg
                      onClick={(e) => isWishReq(e, item._id, setWlist)}
                      className="w-6 h-6 text-yellow-700 cursor-pointer transition-transform transform hover:scale-125"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
                      />
                    </svg>
                  ) : (
                    <svg
                      onClick={(e) => unWishReq(e, item._id, setWlist)}
                      className="w-6 h-6 text-yellow-700 cursor-pointer transition-transform transform hover:scale-125"
                      fill="currentColor"
                      viewBox="0 0 20 20"
                    >
                      <path
                        fillRule="evenodd"
                        d="M3.172 5.172a4 4 0 015.656 0L10 6.343l1.172-1.171a4 4 0 115.656 5.656L10 17.657l-6.828-6.829a4 4 0 010-5.656z"
                        clipRule="evenodd"
                      />
                    </svg>
                  )}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="flex items-center justify-center text-gray-600 text-2xl py-32">
            No products found.
          </div>
        )}
      </div>
    </Fragment>
  );
};

export default Shop;
