import React, { useState } from 'react';
import { sendMessage } from './FetchApi';

const UserSendMessage = ({ user }) => {
  const [message, setMessage] = useState('');
  const [alert, setAlert] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await sendMessage({
      senderId: user._id,
      senderName: user.name,
      email: user.email,
      message
    });

    if (res.success) {
      setAlert('Message sent!');
      setMessage('');
    } else {
      setAlert('Failed to send message');
    }
  };

  return (
    <div className="p-4 bg-white rounded shadow max-w-xl mx-auto">
      <h2 className="text-lg font-bold mb-3">Send a message to admin</h2>
      {alert && <p className="text-green-600 mb-2">{alert}</p>}
      <form onSubmit={handleSubmit}>
        <textarea
          className="w-full border p-2 rounded"
          rows="5"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          required
        />
        <button
          type="submit"
          className="mt-3 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Send Message
        </button>
      </form>
    </div>
  );
};

export default UserSendMessage;
