import React, { Fragment, useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import { motion } from "framer-motion";
import { ProductDetailsContext } from "./index";
import { LayoutContext } from "../layout";
import Submenu from "./Submenu";
import ProductDetailsSectionTwo from "./ProductDetailsSectionTwo";

import { getSingleProduct } from "./FetchApi";
import { cartListProduct } from "../partials/FetchApi";

import { isWishReq, unWishReq, isWish } from "../home/Mixins";
import { updateQuantity, slideImage, addToCart, cartList } from "./Mixins";
import { totalCost } from "../partials/Mixins";

const ProductDetailsSection = () => {
  let { id } = useParams();
  const { data, dispatch } = useContext(ProductDetailsContext);
  const { data: layoutData, dispatch: layoutDispatch } = useContext(LayoutContext);

  const sProduct = layoutData.singleProductDetail;
  const [pImages, setPimages] = useState(null);
  const [count, setCount] = useState(0);
  const [quantitiy, setQuantitiy] = useState(1);
  const [, setAlertq] = useState(false);
  const [wList, setWlist] = useState(JSON.parse(localStorage.getItem("wishList")));
  const [selectedSize, setSelectedSize] = useState("100ml");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    dispatch({ type: "loading", payload: true });
    try {
      let responseData = await getSingleProduct(id);
      setTimeout(() => {
        if (responseData.Product) {
          layoutDispatch({
            type: "singleProductDetail",
            payload: responseData.Product,
          });
          setPimages(responseData.Product.pImages);
          dispatch({ type: "loading", payload: false });
          layoutDispatch({ type: "inCart", payload: cartList() });
        }
        if (responseData.error) console.log(responseData.error);
      }, 500);
    } catch (error) {
      console.log(error);
    }
    fetchCartProduct();
  };

  const fetchCartProduct = async () => {
    try {
      let responseData = await cartListProduct();
      if (responseData?.Products) {
        layoutDispatch({ type: "cartProduct", payload: responseData.Products });
      }
    } catch (error) {
      console.log(error);
    }
  };

  if (data.loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (!sProduct) return <div>No product found</div>;

  return (
    <Fragment>
      <Submenu
        value={{
          categoryId: sProduct.pCategory._id,
          product: sProduct.pName,
          category: sProduct.pCategory.cName,
        }}
      />

      <section className="m-4 md:mx-12 md:my-6">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="grid grid-cols-1 md:grid-cols-2 gap-8"
        >
          <motion.div
            className="relative"
            initial={{ x: -50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <img
              className="w-full h-[450px] object-contain rounded-xl transition-transform duration-300 ease-in-out hover:scale-105 shadow-lg"
              src={`http://localhost:8000/uploads/products/${sProduct.pImages[count]}`}
              alt="Product"
            />
            <div className="absolute inset-0 flex justify-between items-center">
              <button
                onClick={() => slideImage("decrease", null, count, setCount, pImages)}
                className="text-gray-500 hover:text-yellow-600 p-2"
              >
                ◀
              </button>
              <button
                onClick={() => slideImage("increase", null, count, setCount, pImages)}
                className="text-gray-500 hover:text-yellow-600 p-2"
              >
                ▶
              </button>
            </div>
            <div className="flex space-x-2 mt-4 justify-center">
              {pImages?.map((img, index) => (
                <img
                  key={index}
                  onClick={() => setCount(index)}
                  src={`http://localhost:8000/uploads/products/${img}`}
                  alt="Thumb"
                  className={`w-16 h-16 object-cover border-2 rounded-md cursor-pointer transition-transform duration-200 ${
                    count === index ? "border-yellow-600 scale-105" : "border-gray-300"
                  }`}
                />
              ))}
            </div>
          </motion.div>

          <motion.div
            className="space-y-4"
            initial={{ x: 50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <div className="text-3xl font-semibold tracking-wide">
              {sProduct.pName}
            </div>
            <div className="text-yellow-600 flex items-center space-x-2">
              <span>★★★★☆</span>
              <span className="text-sm text-gray-600">(32 reviews)</span>
            </div>

            <div className="text-base text-gray-700 leading-relaxed">
              {sProduct.pDescription}
            </div>

            <div className="flex items-center space-x-4 text-xl">
              {sProduct.discount && (
                <span className="line-through text-gray-400">
                  ${sProduct.pPrice + 20}.00
                </span>
              )}
              <span className="text-yellow-700 font-semibold">
                ${sProduct.pPrice}.00
              </span>
            </div>

            <div>
              <div className="font-medium mb-1">Bottle Size</div>
              <div className="flex space-x-2">
                {["50ml", "100ml", "150ml"].map((size) => (
                  <button
                    key={size}
                    onClick={() => setSelectedSize(size)}
                    className={`px-4 py-1 border rounded-full text-sm ${
                      selectedSize === size
                        ? "bg-yellow-600 text-white"
                        : "bg-white text-gray-700 border-gray-300"
                    }`}
                  >
                    {size}
                  </button>
                ))}
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <button
                  onClick={() =>
                    updateQuantity("decrease", sProduct.pQuantity, quantitiy, setQuantitiy, setAlertq)
                  }
                  className="px-2 py-1 border rounded"
                >
                  -
                </button>
                <span>{quantitiy}</span>
                <button
                  onClick={() =>
                    updateQuantity("increase", sProduct.pQuantity, quantitiy, setQuantitiy, setAlertq)
                  }
                  className="px-2 py-1 border rounded"
                >
                  +
                </button>
              </div>

              {layoutData.inCart?.includes(sProduct._id) ? (
                <button
                  disabled
                  className="px-6 py-2 bg-gray-400 text-white rounded uppercase cursor-not-allowed"
                >
                  In Cart
                </button>
              ) : (
                <button
                  onClick={() =>
                    addToCart(
                      sProduct._id,
                      quantitiy,
                      sProduct.pPrice,
                      layoutDispatch,
                      setQuantitiy,
                      setAlertq,
                      fetchData,
                      totalCost
                    )
                  }
                  className="px-6 py-2 bg-yellow-600 hover:bg-yellow-700 text-white rounded uppercase"
                >
                  Add to Cart
                </button>
              )}

              <button
                onClick={() =>
                  isWish(sProduct._id, wList)
                    ? unWishReq(null, sProduct._id, setWlist)
                    : isWishReq(null, sProduct._id, setWlist)
                }
                className="px-4 py-2 border border-yellow-600 text-yellow-600 hover:bg-yellow-50 rounded"
              >
                {isWish(sProduct._id, wList) ? "Remove Wishlist" : "Add to Wishlist"}
              </button>
            </div>

            {+quantitiy === +sProduct.pQuantity && (
              <div className="text-sm text-red-500 mt-1">Stock limited</div>
            )}
          </motion.div>
        </motion.div>
      </section>

      <ProductDetailsSectionTwo />
    </Fragment>
  );
};

export default ProductDetailsSection;
