import React, { Fragment, useEffect, useContext, useState, useRef } from "react";
import { HomeContext } from "./";
import { sliderImages } from "./Action";
import { prevSlide, nextSlide } from "./Mixins";

const Slider = () => {
  const { data, dispatch } = useContext(HomeContext);
  const [slide, setSlide] = useState(0);
  const sliderInterval = useRef(null);
  const totalSlides = data?.sliderImages?.length || 0;

  useEffect(() => {
    sliderImages(dispatch);
  }, [dispatch]);

  useEffect(() => {
    if (totalSlides > 1) {
      startAutoSlide();
    }
    return () => clearInterval(sliderInterval.current);
  }, [totalSlides]);

  const startAutoSlide = () => {
    sliderInterval.current = setInterval(() => {
      setSlide((prev) => (prev + 1) % totalSlides);
    }, 4000);
  };

  const pauseSlider = () => clearInterval(sliderInterval.current);
  const resumeSlider = () => startAutoSlide();

  const fadeInStyle = {
    animation: "fadeIn 1s ease-in-out forwards",
    opacity: 0,
  };

  return (
    <Fragment>
      <style>
        {`
          @keyframes fadeIn {
            0% { opacity: 0; transform: scale(1.01); }
            100% { opacity: 1; transform: scale(1); }
          }
        `}
      </style>

      <div
        className="relative mt-16 overflow-hidden group"
        onMouseEnter={pauseSlider}
        onMouseLeave={resumeSlider}
        style={{
          width: "100%",
          maxHeight: "500px",
          height: "400px",
          backgroundColor: "#f3f4f6",
        }}
      >
        {/* Slider Image */}
        {totalSlides > 0 && (
          <img
            src={`http://localhost:8000/uploads/customize/${data.sliderImages[slide].slideImage}`}
            alt="sliderImage"
            className="w-full h-full object-contain"
            style={fadeInStyle}
          />
        )}

        {/* Arrows & Button */}
        {totalSlides > 1 && (
          <>
            {/* Left Arrow */}
            <button
              onClick={() => prevSlide(totalSlides, slide, setSlide)}
              className="absolute top-1/2 left-4 transform -translate-y-1/2 bg-black bg-opacity-40 hover:bg-opacity-80 text-white p-2 rounded-full z-10 transition"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" strokeWidth="2"
                viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" />
              </svg>
            </button>

            {/* Right Arrow */}
            <button
              onClick={() => nextSlide(totalSlides, slide, setSlide)}
              className="absolute top-1/2 right-4 transform -translate-y-1/2 bg-black bg-opacity-40 hover:bg-opacity-80 text-white p-2 rounded-full z-10 transition"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" strokeWidth="2"
                viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" d="M9 5l7 7-7 7" />
              </svg>
            </button>

            {/* CTA Button */}
            <div className="absolute inset-0 flex items-center justify-center z-10">
              <a
                href="#shop"
                className="bg-yellow-500 bg-opacity-80 hover:bg-yellow-600 text-black font-semibold px-6 py-3 rounded-md shadow-lg transition"
              >
                Shop Now
              </a>
            </div>
          </>
        )}
      </div>
    </Fragment>
  );
};

export default Slider;
