import React, { Fragment, useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import { ProductDetailsContext } from "./index";
import { LayoutContext } from "../layout";
import Submenu from "./Submenu";
import ProductDetailsSectionTwo from "./ProductDetailsSectionTwo";

import { getSingleProduct } from "./FetchApi";
import { cartListProduct } from "../partials/FetchApi";

import { isWishReq, unWishReq, isWish } from "../home/Mixins";
import { updateQuantity, slideImage, addToCart, cartList } from "./Mixins";
import { totalCost } from "../partials/Mixins";

const ProductDetailsSection = () => {
  const { id } = useParams();
  const { data, dispatch } = useContext(ProductDetailsContext);
  const { data: layoutData, dispatch: layoutDispatch } = useContext(LayoutContext);

  const sProduct = layoutData.singleProductDetail;
  const [pImages, setPimages] = useState([]);
  const [count, setCount] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [, setAlertq] = useState(false);
  const [wList, setWlist] = useState(JSON.parse(localStorage.getItem("wishList")));
  const [selectedSize, setSelectedSize] = useState("50ml");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    dispatch({ type: "loading", payload: true });
    try {
      const responseData = await getSingleProduct(id);
      if (responseData.Product) {
        layoutDispatch({ type: "singleProductDetail", payload: responseData.Product });
        setPimages(responseData.Product.pImages);
        dispatch({ type: "loading", payload: false });
        layoutDispatch({ type: "inCart", payload: cartList() });
      }
    } catch (error) {
      console.log(error);
    }
    fetchCartProduct();
  };

  const fetchCartProduct = async () => {
    try {
      const responseData = await cartListProduct();
      if (responseData?.Products) {
        layoutDispatch({ type: "cartProduct", payload: responseData.Products });
      }
    } catch (error) {
      console.log(error);
    }
  };

  if (data.loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <svg className="w-12 h-12 animate-spin text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
        </svg>
      </div>
    );
  }

  if (!sProduct) return <div>No product</div>;

  return (
    <Fragment>
      <Submenu
        value={{
          categoryId: sProduct.pCategory._id,
          product: sProduct.pName,
          category: sProduct.pCategory.cName,
        }}
      />

      <section className="m-4 md:mx-12 md:my-6">
        <div className="grid grid-cols-1 md:grid-cols-12 gap-6">
          {/* Thumbnails */}
          <div className="hidden md:flex md:flex-col md:space-y-2 md:col-span-1">
            {pImages.map((img, idx) => (
              <img
                key={idx}
                onClick={() => setCount(idx)}
                className={`w-20 h-20 rounded object-cover border cursor-pointer ${count === idx ? 'border-yellow-700' : 'border-gray-300'}`}
                src={`http://localhost:8000/uploads/products/${img}`}
                alt="thumb"
              />
            ))}
          </div>

          {/* Main Image */}
          <div className="col-span-1 md:col-span-7">
            <img
              className="w-full h-[450px] object-cover rounded"
              src={`http://localhost:8000/uploads/products/${pImages[count]}`}
              alt="Main"
            />
          </div>

          {/* Product Details */}
          <div className="col-span-1 md:col-span-4 space-y-4">
            <div className="text-2xl font-semibold text-gray-800">{sProduct.pName}</div>

            {/* Rating and Reviews */}
            <div className="flex items-center space-x-2">
              <svg className="w-5 h-5 text-yellow-600" fill="currentColor" viewBox="0 0 20 20">
                <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.518 4.674h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888 1.518 4.674c.3.922-.755 1.688-1.538 1.118L10 15.347l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674-3.976-2.888c-.783-.57-.38-1.81.588-1.81h4.915l1.518-4.674z" />
              </svg>
              <span className="text-sm text-gray-600">{sProduct.pRatingsReviews.length} reviews</span>
            </div>

            <p className="text-gray-700 text-sm">{sProduct.pDescription}</p>

            {/* Pricing */}
            <div className="flex items-center space-x-2">
              {sProduct.pOffer > 0 && (
                <span className="bg-red-500 text-white px-2 text-xs rounded-full">{sProduct.pOffer}% OFF</span>
              )}
              <span className="text-xl font-bold text-gray-900">${sProduct.pPrice}</span>
              {sProduct.oldPrice && <span className="line-through text-gray-500">${sProduct.oldPrice}</span>}
            </div>

            {/* Bottle Size Options */}
            <div className="flex space-x-2 text-sm">
              {["50ml", "100ml", "150ml"].map((size) => (
                <button
                  key={size}
                  onClick={() => setSelectedSize(size)}
                  className={`px-3 py-1 border rounded-full ${selectedSize === size ? "bg-yellow-600 text-white" : "bg-white text-gray-700 border-gray-400"}`}
                >
                  {size}
                </button>
              ))}
            </div>

            {/* Quantity and Cart */}
            <div className="flex space-x-4 items-center">
              <div className="flex items-center space-x-2 border px-3 py-1 rounded">
                <button onClick={() => updateQuantity("decrease", sProduct.pQuantity, quantity, setQuantity, setAlertq)}>-</button>
                <span>{quantity}</span>
                <button onClick={() => updateQuantity("increase", sProduct.pQuantity, quantity, setQuantity, setAlertq)}>+</button>
              </div>
              <button
                onClick={() =>
                  addToCart(
                    sProduct._id,
                    quantity,
                    sProduct.pPrice,
                    layoutDispatch,
                    setQuantity,
                    setAlertq,
                    fetchData,
                    totalCost
                  )
                }
                className="bg-yellow-600 text-white px-4 py-2 rounded hover:bg-yellow-700"
              >
                Add to Cart
              </button>
            </div>

            {/* Wishlist */}
            <div>
              {!isWish(sProduct._id, wList) ? (
                <button onClick={(e) => isWishReq(e, sProduct._id, setWlist)} className="text-yellow-600 text-sm underline">Add to Wishlist</button>
              ) : (
                <button onClick={(e) => unWishReq(e, sProduct._id, setWlist)} className="text-yellow-600 text-sm underline">Remove from Wishlist</button>
              )}
            </div>
          </div>
        </div>
      </section>

      <ProductDetailsSectionTwo />
    </Fragment>
  );
};

export default ProductDetailsSection;
