// src/pages/Shop.jsx
import React, { Fragment, useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { getAllProduct } from "../home/fetchApi";
import { isWishReq, unWishReq, isWish } from "../home/Mixins";
import { HomeContext } from "../home/index";

const Shop = () => {
  const { data, dispatch } = useContext(HomeContext);
  const { products, loading } = data;
  const navigate = useNavigate();
  const [wList, setWlist] = useState(JSON.parse(localStorage.getItem("wishList")) || []);

  useEffect(() => {
    fetchProducts();
    // eslint-disable-next-line
  }, []);

  const fetchProducts = async () => {
    dispatch({ type: "loading", payload: true });
    try {
      const res = await getAllProduct();
      if (res && res.Products) {
        dispatch({ type: "setProducts", payload: res.Products });
      }
      dispatch({ type: "loading", payload: false });
    } catch (error) {
      console.error("Failed to fetch products:", error);
      dispatch({ type: "loading", payload: false });
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center py-32">
        <svg className="animate-spin h-10 w-10 text-yellow-600" viewBox="0 0 24 24">
          <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
          <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8H4z" />
        </svg>
      </div>
    );
  }

  return (
    <Fragment>
      <div className="container mx-auto px-4 py-10">
        <h2 className="text-3xl font-semibold text-center text-gray-800 mb-8">Explore All Products</h2>

        {products && products.length > 0 ? (
          <div className="grid gap-6 grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
            {products.map((product) => (
              <div
                key={product._id}
                className="bg-white shadow-lg rounded-lg overflow-hidden relative hover:shadow-xl transition-transform duration-300 hover:scale-105"
              >
                <img
                  src={`http://localhost:8000/uploads/products/${product.pImages[0]}`}
                  alt={product.pName}
                  onClick={() => navigate(`/products/${product._id}`)}
                  className="w-full h-56 object-cover cursor-pointer"
                />
                <div className="p-4">
                  <div className="flex justify-between items-center mb-1">
                    <h3 className="text-md font-medium text-gray-700 truncate">{product.pName}</h3>
                    <div className="flex items-center gap-1">
                      <svg
                        className="w-4 h-4 text-yellow-600"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118L4.96 10.1c-.784-.57-.38-1.81.588-1.81h4.915a1 1 0 00.951-.69l1.519-4.674z"
                        />
                      </svg>
                      <span className="text-sm text-gray-600">{product.pRatingsReviews.length}</span>
                    </div>
                  </div>
                  <p className="text-lg font-bold text-gray-900">${product.pPrice}.00</p>
                </div>

                {/* Wishlist Icon */}
                <div className="absolute top-2 right-2">
                  {!isWish(product._id, wList) ? (
                    <svg
                      onClick={(e) => isWishReq(e, product._id, setWlist)}
                      className="w-6 h-6 text-yellow-700 cursor-pointer transition-transform hover:scale-125"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
                      />
                    </svg>
                  ) : (
                    <svg
                      onClick={(e) => unWishReq(e, product._id, setWlist)}
                      className="w-6 h-6 text-yellow-700 cursor-pointer transition-transform hover:scale-125"
                      fill="currentColor"
                      viewBox="0 0 20 20"
                    >
                      <path
                        fillRule="evenodd"
                        d="M3.172 5.172a4 4 0 015.656 0L10 6.343l1.172-1.171a4 4 0 115.656 5.656L10 17.657l-6.828-6.829a4 4 0 010-5.656z"
                        clipRule="evenodd"
                      />
                    </svg>
                  )}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center text-gray-500 text-xl mt-20">No products found.</div>
        )}
      </div>
    </Fragment>
  );
};

export default Shop;
