import React, { useEffect, useState } from 'react';
import { getAllMessages } from './FetchApi';

const Message = () => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const fetchMessages = async () => {
      const res = await getAllMessages();
      if (res.success) setMessages(res.messages);
    };
    fetchMessages();
  }, []);

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h2 className="text-xl font-bold mb-4">User Messages</h2>
      <div className="space-y-4">
        {messages.map((msg) => (
          <div key={msg._id} className="border p-4 rounded shadow-sm bg-gray-50">
            <div className="flex justify-between">
              <h3 className="font-semibold">{msg.senderName} ({msg.email})</h3>
              <span className="text-sm text-gray-500">{new Date(msg.createdAt).toLocaleString()}</span>
            </div>
            <p className="mt-2 text-gray-700">{msg.message}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Message;
