import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import {
  clearAllMessageErrors,
  deleteMessage,
  getAllMessages,
  resetMessagesSlice,
} from "./messageSlice";

const Messages = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { messages, loading, error, message } = useSelector((state) => state.messages);

  const [messageId, setMessageId] = useState("");
  const [localError, setLocalError] = useState("");
  const [localSuccess, setLocalSuccess] = useState("");

  const handleReturn = () => navigate("/");

  const handleDelete = (id) => {
    setMessageId(id);
    dispatch(deleteMessage(id));
  };

  useEffect(() => {
    dispatch(getAllMessages());
  }, [dispatch]);

  useEffect(() => {
    if (error) {
      setLocalError(error);
      setTimeout(() => setLocalError(""), 3000);
      dispatch(clearAllMessageErrors());
    }
    if (message) {
      setLocalSuccess(message);
      setTimeout(() => setLocalSuccess(""), 3000);
      dispatch(resetMessagesSlice());
      dispatch(getAllMessages());
    }
  }, [dispatch, error, message]);

  return (
    <div className="min-h-screen p-6 bg-gray-100">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-xl font-semibold">Messages</h1>
        <button onClick={handleReturn} className="bg-blue-600 text-white px-4 py-2 rounded">
          Return to Dashboard
        </button>
      </div>

      {localError && <p className="text-red-600 mb-2">{localError}</p>}
      {localSuccess && <p className="text-green-600 mb-2">{localSuccess}</p>}

      <div className="grid sm:grid-cols-2 gap-4">
        {messages.length > 0 ? (
          messages.map((msg) => (
            <div key={msg._id} className="bg-white p-4 rounded shadow">
              <p><strong>Sender:</strong> {msg.senderName}</p>
              <p><strong>Subject:</strong> {msg.subject}</p>
              <p><strong>Message:</strong> {msg.message}</p>
              <div className="flex justify-end mt-2">
                <button
                  onClick={() => handleDelete(msg._id)}
                  className="bg-red-500 text-white px-4 py-1 rounded"
                  disabled={loading && messageId === msg._id}
                >
                  {loading && messageId === msg._id ? "Deleting..." : "Delete"}
                </button>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center col-span-2 text-gray-500">No messages found.</p>
        )}
      </div>
    </div>
  );
};

export default Messages;
