import React, { Fragment, useContext, useState, useEffect } from "react";
import { ProductContext } from "./index";
import { createProduct, getAllProduct } from "./FetchApi";
import { getAllCategory } from "../categories/FetchApi";

const AddProductDetail = ({ categories }) => {
  const { data, dispatch } = useContext(ProductContext);

  const alert = (msg, type) => (
    <div className={`bg-${type}-200 py-2 px-4 w-full`}>{msg}</div>
  );

  const [fData, setFdata] = useState({
    pName: "",
    pDescription: "",
    pStatus: "Active",
    pImage: null,
    pCategory: "",
    oldPrice: "",
    pPrice: "",
    pOffer: 0,
    pQuantity: "",
    topCollection: false,
    solarPerfume: false,
    discount: false,
    seasonalProduct: false,
    animation: false,
    success: false,
    error: false,
  });

  const fetchData = async () => {
    const responseData = await getAllProduct();
    if (responseData?.Products) {
      dispatch({
        type: "fetchProductsAndChangeState",
        payload: responseData.Products,
      });
    }
  };

  const submitForm = async (e) => {
    e.preventDefault();

    if (!fData.pImage || fData.pImage.length < 2) {
      setFdata({ ...fData, error: "Please upload at least 2 images" });
      setTimeout(() => {
        setFdata((prev) => ({ ...prev, error: false }));
      }, 2000);
      return;
    }

    try {
      const responseData = await createProduct(fData);
      if (responseData?.success) {
        fetchData();
        setFdata({
          pName: "",
          pDescription: "",
          pStatus: "Active",
          pImage: null,
          pCategory: "",
          oldPrice: "",
          pPrice: "",
          pOffer: 0,
          pQuantity: "",
          topCollection: false,
          solarPerfume: false,
          discount: false,
          seasonalProduct: false,
          animation: false,
          success: responseData.success,
          error: false,
        });

        setTimeout(() => {
          setFdata((prev) => ({ ...prev, success: false }));
        }, 2000);
      } else {
        setFdata({
          ...fData,
          error: responseData?.error || "Something went wrong",
          success: false,
        });
        setTimeout(() => {
          setFdata((prev) => ({ ...prev, error: false }));
        }, 2000);
      }
    } catch (error) {
      console.log("Create Product Error:", error);
      setFdata({
        ...fData,
        error: "Internal Server Error",
        success: false,
      });
      setTimeout(() => {
        setFdata((prev) => ({ ...prev, error: false }));
      }, 2000);
    }
  };

  return (
    <Fragment>
      {/* Overlay */}
      <div
        onClick={() => dispatch({ type: "addProductModal", payload: false })}
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed top-0 left-0 z-30 w-full h-full bg-black opacity-50`}
      />

      {/* Modal */}
      <div
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed inset-0 flex items-center z-30 justify-center overflow-auto`}
      >
        <div className="mt-32 md:mt-0 bg-white w-11/12 md:w-3/6 shadow-lg flex flex-col px-6 py-6 rounded-lg">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-semibold">Add Product</h2>
            <button
              onClick={() => dispatch({ type: "addProductModal", payload: false })}
              className="text-white bg-black p-2 rounded-full"
            >
              ✕
            </button>
          </div>

          {fData.error && alert(fData.error, "red")}
          {fData.success && alert("Product created successfully!", "green")}

          <form onSubmit={submitForm}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <input
                type="text"
                placeholder="Product Name"
                value={fData.pName}
                onChange={(e) => setFdata({ ...fData, pName: e.target.value })}
                className="border px-4 py-2"
                required
              />
              <input
                type="number"
                placeholder="Old Price"
                value={fData.oldPrice}
                onChange={(e) => setFdata({ ...fData, oldPrice: e.target.value })}
                className="border px-4 py-2"
              />
              <input
                type="number"
                placeholder="Current Price"
                value={fData.pPrice}
                onChange={(e) => setFdata({ ...fData, pPrice: e.target.value })}
                className="border px-4 py-2"
              />
              <input
                type="number"
                placeholder="Offer %"
                value={fData.pOffer}
                onChange={(e) => setFdata({ ...fData, pOffer: e.target.value })}
                className="border px-4 py-2"
              />
              <input
                type="number"
                placeholder="Quantity"
                value={fData.pQuantity}
                onChange={(e) => setFdata({ ...fData, pQuantity: e.target.value })}
                className="border px-4 py-2"
              />
              <select
                value={fData.pStatus}
                onChange={(e) => setFdata({ ...fData, pStatus: e.target.value })}
                className="border px-4 py-2"
              >
                <option value="Active">Active</option>
                <option value="Disabled">Disabled</option>
              </select>
              <select
                value={fData.pCategory}
                onChange={(e) => setFdata({ ...fData, pCategory: e.target.value })}
                className="border px-4 py-2"
              >
                <option value="">Select Category</option>
                {categories?.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.cName}
                  </option>
                ))}
              </select>
              <input
                type="file"
                accept="image/*"
                multiple
                onChange={(e) =>
                  setFdata({ ...fData, pImage: [...e.target.files] })
                }
                className="border px-4 py-2"
              />
            </div>

            <textarea
              placeholder="Product Description"
              value={fData.pDescription}
              onChange={(e) => setFdata({ ...fData, pDescription: e.target.value })}
              className="border px-4 py-2 mt-4 w-full"
              rows={3}
            ></textarea>

            <div className="grid grid-cols-2 md:grid-cols-3 gap-2 mt-4 text-sm">
              {[
                "topCollection",
                "solarPerfume",
                "discount",
                "seasonalProduct",
                "animation",
              ].map((field) => (
                <label key={field} className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={fData[field]}
                    onChange={(e) =>
                      setFdata({ ...fData, [field]: e.target.checked })
                    }
                  />
                  <span className="capitalize">{field}</span>
                </label>
              ))}
            </div>

            <button
              type="submit"
              className="bg-black text-white px-6 py-2 mt-6 rounded-full"
            >
              Create Product
            </button>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

const AddProductModal = () => {
  const [allCat, setAllCat] = useState([]);

  useEffect(() => {
    const fetchCategoryData = async () => {
      const responseData = await getAllCategory();
      if (responseData?.Categories) {
        setAllCat(responseData.Categories);
      }
    };
    fetchCategoryData();
  }, []);

  return (
    <Fragment>
      <AddProductDetail categories={allCat} />
    </Fragment>
  );
};

export default AddProductModal;
