// AdminAboutEdit.jsx
import React, { useEffect, useState } from "react";

const AdmimABout = () => {
  const [aboutData, setAboutData] = useState({
    storeName: "",
    description: "",
    mapEmbedUrl: "",
    storeLogo: "",
    team: [],
    socialLinks: []
  });

  const [message, setMessage] = useState("");

  useEffect(() => {
    fetch("/api/about")
      .then((res) => res.json())
      .then((data) => setAboutData(data));
  }, []);

  const handleChange = (e) => {
    setAboutData({ ...aboutData, [e.target.name]: e.target.value });
  };

  const handleTeamChange = (index, key, value) => {
    const updatedTeam = [...aboutData.team];
    updatedTeam[index][key] = value;
    setAboutData({ ...aboutData, team: updatedTeam });
  };

  const handleSocialChange = (index, key, value) => {
    const updatedSocial = [...aboutData.socialLinks];
    updatedSocial[index][key] = value;
    setAboutData({ ...aboutData, socialLinks: updatedSocial });
  };

  const handleSubmit = async () => {
    const res = await fetch(`/api/about/${aboutData._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(aboutData),
    });
    const data = await res.json();
    setMessage("About info updated successfully!");
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow">
      <h2 className="text-2xl font-bold mb-6">Edit About Us Information</h2>
      {message && <p className="text-green-500 mb-4">{message}</p>}

      <label className="block mb-2 font-medium">Store Name</label>
      <input
        type="text"
        name="storeName"
        value={aboutData.storeName}
        onChange={handleChange}
        className="w-full p-2 border rounded mb-4"
      />

      <label className="block mb-2 font-medium">Description</label>
      <textarea
        name="description"
        value={aboutData.description}
        onChange={handleChange}
        className="w-full p-2 border rounded mb-4"
      ></textarea>

      <label className="block mb-2 font-medium">Google Map Embed URL</label>
      <input
        type="text"
        name="mapEmbedUrl"
        value={aboutData.mapEmbedUrl}
        onChange={handleChange}
        className="w-full p-2 border rounded mb-4"
      />

      <h3 className="text-xl font-semibold mt-6 mb-2">Team Members</h3>
      {aboutData.team.map((member, index) => (
        <div key={index} className="mb-4 p-2 border rounded">
          <input
            type="text"
            placeholder="Name"
            value={member.name}
            onChange={(e) => handleTeamChange(index, "name", e.target.value)}
            className="w-full p-1 mb-2 border rounded"
          />
          <input
            type="text"
            placeholder="Role"
            value={member.role}
            onChange={(e) => handleTeamChange(index, "role", e.target.value)}
            className="w-full p-1 mb-2 border rounded"
          />
          <input
            type="text"
            placeholder="Image URL"
            value={member.image}
            onChange={(e) => handleTeamChange(index, "image", e.target.value)}
            className="w-full p-1 mb-2 border rounded"
          />
        </div>
      ))}

      <h3 className="text-xl font-semibold mt-6 mb-2">Social Links</h3>
      {aboutData.socialLinks.map((link, index) => (
        <div key={index} className="mb-4 p-2 border rounded">
          <input
            type="text"
            placeholder="Platform"
            value={link.platform}
            onChange={(e) => handleSocialChange(index, "platform", e.target.value)}
            className="w-full p-1 mb-2 border rounded"
          />
          <input
            type="text"
            placeholder="URL"
            value={link.url}
            onChange={(e) => handleSocialChange(index, "url", e.target.value)}
            className="w-full p-1 mb-2 border rounded"
          />
          <input
            type="text"
            placeholder="Icon Class (e.g., fa fa-facebook)"
            value={link.icon}
            onChange={(e) => handleSocialChange(index, "icon", e.target.value)}
            className="w-full p-1 mb-2 border rounded"
          />
        </div>
      ))}

      <label className="block mb-2 font-medium">Store Logo URL</label>
      <input
        type="text"
        name="storeLogo"
        value={aboutData.storeLogo}
        onChange={handleChange}
        className="w-full p-2 border rounded mb-6"
      />

      <button
        onClick={handleSubmit}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Save Changes
      </button>
    </div>
  );
};

export default AdmimABout;
