import React, { Fragment, useContext, useState, useEffect } from "react";
import { ProductContext } from "./index";
import { editProduct, getAllProduct } from "./FetchApi";
import { getAllCategory } from "../categories/FetchApi";

const EditProductModal = (props) => {
  const { data, dispatch } = useContext(ProductContext);
  const [categories, setCategories] = useState([]);

  const alert = (msg, type) => (
    <div className={`bg-${type}-200 py-2 px-4 w-full`}>{msg}</div>
  );

  const [editformData, setEditformdata] = useState({
    pId: "",
    pName: "",
    pDescription: "",
    pImages: [],
    pEditImages: [],
    pStatus: "",
    pCategory: "",
    pQuantity: "",
    oldPrice: "",
    pPrice: "",
    pOffer: "",
    topCollection: false,
    solarPerfume: false,
    discount: false,
    seasonalProduct: false,
    animation: false,
    error: false,
    success: false,
  });

  useEffect(() => {
    const fetchCategoryData = async () => {
      let responseData = await getAllCategory();
      if (responseData.Categories) {
        setCategories(responseData.Categories);
      }
    };
    fetchCategoryData();
  }, []);

  useEffect(() => {
    const product = data.editProductModal;
    setEditformdata({
      pId: product.pId,
      pName: product.pName,
      pDescription: product.pDescription,
      pImages: product.pImages,
      pStatus: product.pStatus,
      pCategory: product.pCategory,
      pQuantity: product.pQuantity,
      oldPrice: product.oldPrice,
      pPrice: product.pPrice,
      pOffer: product.pOffer,
      topCollection: product.topCollection,
      solarPerfume: product.solarPerfume,
      discount: product.discount,
      seasonalProduct: product.seasonalProduct,
      animation: product.animation,
      pEditImages: [],
      error: false,
      success: false,
    });
  }, [data.editProductModal]);

  const fetchData = async () => {
    let responseData = await getAllProduct();
    if (responseData && responseData.Products) {
      dispatch({
        type: "fetchProductsAndChangeState",
        payload: responseData.Products,
      });
    }
  };

  const submitForm = async (e) => {
    e.preventDefault();
    try {
      const responseData = await editProduct(editformData);
      if (responseData.success) {
        fetchData();
        setEditformdata((prev) => ({ ...prev, success: responseData.success }));
        setTimeout(() => {
          setEditformdata((prev) => ({ ...prev, success: false }));
        }, 2000);
      } else if (responseData.error) {
        setEditformdata((prev) => ({ ...prev, error: responseData.error }));
        setTimeout(() => {
          setEditformdata((prev) => ({ ...prev, error: false }));
        }, 2000);
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Fragment>
      <div
        onClick={() => dispatch({ type: "editProductModalClose", payload: false })}
        className={`$${data.editProductModal.modal ? "" : "hidden"} fixed top-0 left-0 z-30 w-full h-full bg-black opacity-50`}
      />

      <div
        className={`$${data.editProductModal.modal ? "" : "hidden"} fixed inset-0 flex items-center z-30 justify-center overflow-auto`}
      >
        <div className="mt-32 md:mt-0 relative bg-white w-11/12 md:w-3/6 shadow-lg flex flex-col items-center space-y-4 px-4 py-4 md:px-8">
          <div className="flex items-center justify-between w-full pt-4">
            <span className="text-left font-semibold text-2xl tracking-wider">
              Edit Product
            </span>
            <span
              style={{ background: "#303031" }}
              onClick={() => dispatch({ type: "editProductModalClose", payload: false })}
              className="cursor-pointer text-gray-100 py-2 px-2 rounded-full"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </span>
          </div>

          {editformData.error && alert(editformData.error, "red")}
          {editformData.success && alert("Product updated successfully!", "green")}

          <form className="w-full" onSubmit={submitForm}>
            <div className="flex space-x-1 py-4">
              <div className="w-1/2 flex flex-col space-y-1">
                <label>Product Name *</label>
                <input
                  value={editformData.pName}
                  onChange={(e) => setEditformdata({ ...editformData, pName: e.target.value })}
                  className="px-4 py-2 border focus:outline-none"
                  type="text"
                />
              </div>
              <div className="w-1/2 flex flex-col space-y-1">
                <label>Old Price *</label>
                <input
                  value={editformData.oldPrice}
                  onChange={(e) => setEditformdata({ ...editformData, oldPrice: e.target.value })}
                  className="px-4 py-2 border focus:outline-none"
                  type="number"
                />
              </div>
            </div>

            <div className="flex space-x-1 py-4">
              <div className="w-1/2 flex flex-col space-y-1">
                <label>New Price *</label>
                <input
                  value={editformData.pPrice}
                  onChange={(e) => setEditformdata({ ...editformData, pPrice: e.target.value })}
                  className="px-4 py-2 border focus:outline-none"
                  type="number"
                />
              </div>
              <div className="w-1/2 flex flex-col space-y-1">
                <label>Quantity in Stock *</label>
                <input
                  value={editformData.pQuantity}
                  onChange={(e) => setEditformdata({ ...editformData, pQuantity: e.target.value })}
                  className="px-4 py-2 border focus:outline-none"
                  type="number"
                />
              </div>
            </div>

            <div className="flex flex-col space-y-2">
              <label>Product Description *</label>
              <textarea
                value={editformData.pDescription}
                onChange={(e) => setEditformdata({ ...editformData, pDescription: e.target.value })}
                className="px-4 py-2 border focus:outline-none"
                rows={2}
              />
            </div>

            <div className="flex flex-col mt-4">
              <label>Product Images *</label>
              {editformData.pImages && editformData.pImages.length > 0 && (
                <div className="flex space-x-1 mt-2">
                  {editformData.pImages.map((img, idx) => (
                    <img
                      key={idx}
                      className="h-16 w-16 object-cover"
                      src={`http://localhost:8000/uploads/products/${img}`}
                      alt={`product-${idx}`}
                    />
                  ))}
                </div>
              )}
              <span className="text-gray-600 text-xs">Upload 2 new images if you want to update</span>
              <input
                onChange={(e) => setEditformdata({ ...editformData, pEditImages: [...e.target.files] })}
                type="file"
                accept=".jpg,.jpeg,.png"
                multiple
                className="px-4 py-2 border focus:outline-none"
              />
            </div>

            <div className="flex space-x-2 py-4">
              <div className="w-1/2 flex flex-col">
                <label>Product Status *</label>
                <select
                  value={editformData.pStatus}
                  onChange={(e) => setEditformdata({ ...editformData, pStatus: e.target.value })}
                  className="px-4 py-2 border focus:outline-none"
                >
                  <option value="Active">Active</option>
                  <option value="Disabled">Disabled</option>
                </select>
              </div>
              <div className="w-1/2 flex flex-col">
                <label>Product Category *</label>
                <select
                  value={editformData.pCategory._id || editformData.pCategory}
                  onChange={(e) => setEditformdata({ ...editformData, pCategory: e.target.value })}
                  className="px-4 py-2 border focus:outline-none"
                >
                  <option value="">Select a category</option>
                  {categories.map((cat) => (
                    <option key={cat._id} value={cat._id}>
                      {cat.cName}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="grid grid-cols-3 gap-3 py-2 text-sm">
              {["topCollection", "solarPerfume", "discount", "seasonalProduct", "animation"].map((name) => (
                <label key={name} className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={editformData[name]}
                    onChange={() => setEditformdata((prev) => ({ ...prev, [name]: !prev[name] }))}
                  />
                  <span>{name.replace(/([A-Z])/g, " $1")}</span>
                </label>
              ))}
            </div>

            <div className="flex flex-col mt-4">
              <label>Offer (%)</label>
              <input
                value={editformData.pOffer}
                onChange={(e) => setEditformdata({ ...editformData, pOffer: e.target.value })}
                type="number"
                className="px-4 py-2 border focus:outline-none"
              />
            </div>

            <div className="flex flex-col space-y-1 w-full pb-4 mt-6">
              <button
                style={{ background: "#303031" }}
                type="submit"
                className="rounded-full bg-gray-800 text-gray-100 text-lg font-medium py-2"
              >
                Update Product
              </button>
            </div>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

export default EditProductModal;
