import React, { Fragment, useContext } from "react";
import { useNavigate } from "react-router-dom";
import "./style.css";

import { LayoutContext } from "../index";
import { isAdmin } from "../auth/fetchApi";

const Navber = () => {
  const navigate = useNavigate();
  const { data, dispatch } = useContext(LayoutContext);

  const isLoggedIn = localStorage.getItem("jwt");

  const loginModalOpen = () =>
    dispatch({ type: "loginSignupModalToggle", payload: !data.loginSignupModal });

  const handleUserIconClick = () => {
    if (isLoggedIn && isAdmin()) {
      navigate("/admin/dashboard");
    } else {
      loginModalOpen();
    }
  };

  return (
    <Fragment>
      <nav className="fixed top-0 w-full z-20 shadow-lg bg-white">
        <div className="m-4 md:mx-12 md:my-6 grid grid-cols-2 items-center">
          {/* Left Side: Logo */}
          <div className="flex items-center">
            <span
              onClick={() => navigate("/")}
              className="font-bold uppercase text-gray-800 text-2xl cursor-pointer tracking-widest"
            >
              Hayroo
            </span>
          </div>

          {/* Right Side: Login or User Icon */}
          <div className="flex justify-end items-center">
            <div
              onClick={handleUserIconClick}
              className="cursor-pointer hover:bg-gray-200 px-2 py-2 rounded-full"
              title={isLoggedIn ? "Admin Panel" : "Login"}
            >
              {isLoggedIn ? (
                <svg
                  className="w-8 h-8 text-gray-600 hover:text-gray-800"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M5.121 17.804A13.937 13.937 0 0112 16c2.5 0 4.847.655 6.879 1.804M15 10a3 3 0 11-6 0 3 3 0 016 0zm6 2a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                </svg>
              ) : (
                <img
                  src="/images/login-icon.png" // your uploaded image, place it in public/images
                  alt="Login"
                  className="w-8 h-8 object-cover"
                />
              )}
            </div>
          </div>
        </div>
      </nav>
    </Fragment>
  );
};

export default Navber;
