import React, { useState } from "react";

const AdminDashboard = () => {
  const [images, setImages] = useState([]);

  const handleUpload = (e) => {
    const files = Array.from(e.target.files);
    const newImages = files.map((file) => URL.createObjectURL(file));
    setImages([...images, ...newImages]);
  };

  const removeImage = (index) => {
    const filtered = images.filter((_, i) => i !== index);
    setImages(filtered);
  };

  return (
    <div className="flex flex-col md:flex-row bg-gray-100 min-h-screen">
      {/* Sidebar */}
      <aside className="w-full md:w-64 bg-white shadow-md p-4">
        <h2 className="text-xl font-bold mb-6">Admin Panel</h2>
        <nav className="space-y-4 text-gray-700">
          <a href="/admin/dashboard" className="block hover:text-blue-600">Dashboard</a>
          <a href="/admin/categories" className="block hover:text-blue-600">Categories</a>
          <a href="/admin/products" className="block hover:text-blue-600">Products</a>
          <a href="/admin/orders" className="block hover:text-blue-600">Orders</a>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-4 md:p-6 space-y-6">
        {/* Cards Section */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
          <div className="bg-white p-4 rounded shadow text-center">
            <div className="text-gray-600">Customers</div>
            <div className="text-green-500">7% ↑</div>
          </div>
          <div className="bg-white p-4 rounded shadow text-center">
            <div className="text-gray-600">Orders</div>
            <div className="text-green-500">10% ↑</div>
          </div>
          <div className="bg-white p-4 rounded shadow text-center">
            <div className="text-gray-600">Products</div>
          </div>
          <div className="bg-white p-4 rounded shadow text-center">
            <div className="text-gray-600">Categories</div>
          </div>
        </div>

        {/* Shop Slider Images */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-3">Shop Slider Images</h2>
          <input
            type="file"
            multiple
            onChange={handleUpload}
            className="mb-4 w-full p-2 border border-gray-300 rounded"
          />
          <div className="flex flex-wrap gap-3">
            {images.map((img, index) => (
              <div key={index} className="relative w-40">
                <img
                  src={img}
                  alt={`Uploaded ${index}`}
                  className="w-full h-24 object-cover rounded"
                />
                <button
                  onClick={() => removeImage(index)}
                  className="absolute top-1 right-1 bg-red-500 text-white text-xs px-2 py-0.5 rounded"
                >
                  ✕
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Orders Table */}
        <div className="bg-white p-4 rounded shadow overflow-x-auto">
          <h2 className="text-lg font-semibold mb-3">Today's Orders (0)</h2>
          <table className="min-w-full text-sm border">
            <thead>
              <tr className="bg-gray-100 text-left">
                <th className="p-2 border">Products</th>
                <th className="p-2 border">Image</th>
                <th className="p-2 border">Status</th>
                <th className="p-2 border">Order Address</th>
                <th className="p-2 border">Ordered At</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="p-3 border text-center" colSpan="5">
                  Total 0 orders found
                </td>
              </tr>
            </tbody>
          </table>
          <div className="text-center mt-4">
            <button className="bg-black text-white px-4 py-2 rounded hover:bg-gray-800">
              View All
            </button>
          </div>
        </div>
      </main>
    </div>
  );
};

export default AdminDashboard;
