export const layoutState = {
  loginSignupModal: false,
};

export const layoutReducer = (state, action) => {
  switch (action.type) {
    case "loginSignupModalToggle":
      return { ...state, loginSignupModal: action.payload };
    default:
      return state;
  }
};
