import React, { Fragment, useContext, useState, useEffect } from "react";
import { ProductContext } from "./index";
import { createProduct, getAllProduct } from "./FetchApi";
import { getAllCategory } from "../categories/FetchApi";

const AddProductDetail = ({ categories }) => {
  const { data, dispatch } = useContext(ProductContext);

  const alert = (msg, type) => (
    <div className={`bg-${type}-200 py-2 px-4 w-full`}>{msg}</div>
  );

  const [fData, setFdata] = useState({
    pName: "",
    pDescription: "",
    oldPrice: "",
    newPrice: "",
    pStatus: "Active",
    pImage: [],
    pCategory: "",
    pOffer: "",
    pQuantity: "",
    topCollection: false,
    solarPerfume: false,
    discount: false,
    seasonalProduct: false,
    animation: false,
    success: false,
    error: false,
  });

  const fetchData = async () => {
    let responseData = await getAllProduct();
    if (responseData && responseData.Products) {
      dispatch({
        type: "fetchProductsAndChangeState",
        payload: responseData.Products,
      });
    }
  };

  const submitForm = async (e) => {
    e.preventDefault();

    if (!fData.pImage || fData.pImage.length !== 2) {
      setFdata({ ...fData, error: "Please upload exactly 2 images" });
      return setTimeout(() => {
        setFdata({ ...fData, error: false });
      }, 2000);
    }

    const formData = new FormData();
    formData.append("pName", fData.pName);
    formData.append("pDescription", fData.pDescription);
    formData.append("oldPrice", fData.oldPrice);
    formData.append("newPrice", fData.newPrice);
    formData.append("pStatus", fData.pStatus);
    formData.append("pCategory", fData.pCategory);
    formData.append("pOffer", fData.pOffer);
    formData.append("pQuantity", fData.pQuantity);

    // ✅ Boolean values
    formData.append("topCollection", fData.topCollection);
    formData.append("solarPerfume", fData.solarPerfume);
    formData.append("discount", fData.discount);
    formData.append("seasonalProduct", fData.seasonalProduct);
    formData.append("animation", fData.animation);

    // ✅ Multiple image files
    fData.pImage.forEach((file) => {
      formData.append("pImage", file);
    });

    try {
      const responseData = await createProduct(formData);
      if (responseData.success) {
        fetchData();
        setFdata({
          pName: "",
          pDescription: "",
          oldPrice: "",
          newPrice: "",
          pStatus: "Active",
          pImage: [],
          pCategory: "",
          pOffer: "",
          pQuantity: "",
          topCollection: false,
          solarPerfume: false,
          discount: false,
          seasonalProduct: false,
          animation: false,
          success: responseData.success,
          error: false,
        });
        setTimeout(() => {
          setFdata((prev) => ({ ...prev, success: false }));
        }, 2000);
      } else {
        setFdata({ ...fData, success: false, error: responseData.error });
        setTimeout(() => {
          setFdata((prev) => ({ ...prev, error: false }));
        }, 2000);
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <Fragment>
      <div
        onClick={() => dispatch({ type: "addProductModal", payload: false })}
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed top-0 left-0 z-30 w-full h-full bg-black opacity-50`}
      />
      <div
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed inset-0 flex items-center z-30 justify-center overflow-auto`}
      >
        <div className="mt-32 md:mt-0 relative bg-white w-11/12 md:w-3/6 shadow-lg flex flex-col items-center space-y-4 px-4 py-4 md:px-8">
          <div className="flex items-center justify-between w-full pt-4">
            <span className="text-left font-semibold text-2xl tracking-wider">
              Add Product
            </span>
            <span
              style={{ background: "#303031" }}
              onClick={() =>
                dispatch({ type: "addProductModal", payload: false })
              }
              className="cursor-pointer text-gray-100 py-2 px-2 rounded-full"
            >
              ✖
            </span>
          </div>

          {fData.error && alert(fData.error, "red")}
          {fData.success && alert(fData.success, "green")}

          <form className="w-full" onSubmit={submitForm}>
            <div className="flex space-x-1 py-4">
              <input
                value={fData.pName}
                onChange={(e) => setFdata({ ...fData, pName: e.target.value })}
                placeholder="Product Name *"
                className="w-1/2 px-4 py-2 border"
              />
              <input
                value={fData.oldPrice}
                onChange={(e) =>
                  setFdata({ ...fData, oldPrice: e.target.value })
                }
                type="number"
                placeholder="Old Price *"
                className="w-1/4 px-4 py-2 border"
              />
              <input
                value={fData.newPrice}
                onChange={(e) =>
                  setFdata({ ...fData, newPrice: e.target.value })
                }
                type="number"
                placeholder="New Price *"
                className="w-1/4 px-4 py-2 border"
              />
            </div>

            <textarea
              value={fData.pDescription}
              onChange={(e) =>
                setFdata({ ...fData, pDescription: e.target.value })
              }
              placeholder="Description *"
              className="w-full px-4 py-2 border my-2"
              rows={3}
            />

            <input
              type="file"
              accept="image/*"
              onChange={(e) =>
                setFdata({ ...fData, pImage: [...e.target.files] })
              }
              className="w-full px-4 py-2 border"
              multiple
            />

            <div className="flex space-x-2 mt-2">
              <select
                value={fData.pStatus}
                onChange={(e) =>
                  setFdata({ ...fData, pStatus: e.target.value })
                }
                className="w-1/2 px-4 py-2 border"
              >
                <option value="Active">Active</option>
                <option value="Disabled">Disabled</option>
              </select>

              <select
                value={fData.pCategory}
                onChange={(e) =>
                  setFdata({ ...fData, pCategory: e.target.value })
                }
                className="w-1/2 px-4 py-2 border"
              >
                <option disabled value="">
                  Select Category
                </option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.cName}
                  </option>
                ))}
              </select>
            </div>

            <div className="flex space-x-2 mt-2">
              <input
                value={fData.pQuantity}
                onChange={(e) =>
                  setFdata({ ...fData, pQuantity: e.target.value })
                }
                type="number"
                placeholder="Quantity *"
                className="w-1/2 px-4 py-2 border"
              />
              <input
                value={fData.pOffer}
                onChange={(e) =>
                  setFdata({ ...fData, pOffer: e.target.value })
                }
                type="number"
                placeholder="Offer %"
                className="w-1/2 px-4 py-2 border"
              />
            </div>

            {/* ✅ Boolean fields */}
            <div className="grid grid-cols-3 gap-2 mt-4 text-sm">
              {[
                "topCollection",
                "solarPerfume",
                "discount",
                "seasonalProduct",
                "animation",
              ].map((field) => (
                <label key={field} className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={fData[field]}
                    onChange={(e) =>
                      setFdata({ ...fData, [field]: e.target.checked })
                    }
                  />
                  <span>{field}</span>
                </label>
              ))}
            </div>

            <button
              type="submit"
              className="mt-6 w-full py-2 bg-gray-800 text-white rounded-full"
            >
              Create Product
            </button>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

const AddProductModal = (props) => {
  useEffect(() => {
    fetchCategoryData();
  }, []);

  const [allCat, setAllCat] = useState({});

  const fetchCategoryData = async () => {
    let responseData = await getAllCategory();
    if (responseData.Categories) {
      setAllCat(responseData.Categories);
    }
  };

  return (
    <Fragment>
      <AddProductDetail categories={allCat} />
    </Fragment>
  );
};

export default AddProductModal;
