import React, { Fragment, useContext } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./style.css";

import { logout } from "./Action";
import { LayoutContext } from "../index";
import { isAdmin } from "../auth/fetchApi";

const Navber = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { data, dispatch } = useContext(LayoutContext);

  const navberToggleOpen = () =>
    dispatch({ type: "hamburgerToggle", payload: !data.navberHamburger });

  const loginModalOpen = () =>
    dispatch({ type: "loginSignupModalToggle", payload: !data.loginSignupModal });

  return (
    <Fragment>
      <nav className="fixed top-0 w-full z-20 shadow-lg lg:shadow-none bg-white">
        <div className="m-4 md:mx-12 md:my-6 grid grid-cols-2 items-center">
          {/* Brand Name on the Left */}
          <div
            onClick={() => navigate("/")}
            className="text-left text-gray-800 font-bold tracking-widest uppercase text-2xl cursor-pointer"
          >
            Hayroo
          </div>

          {/* Right Side: Login/User Icon */}
          <div className="flex justify-end items-center">
            {localStorage.getItem("jwt") ? (
              <div className="relative">
                <svg
                  className="cursor-pointer w-8 h-8 text-gray-600 hover:text-gray-800"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M5.121 17.804A13.937 13.937 0 0112 16c2.5 0 4.847.655 6.879 1.804M15 10a3 3 0 11-6 0 3 3 0 016 0zm6 2a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                </svg>
                <div className="absolute right-0 mt-1 bg-gray-200 rounded w-48 shadow-lg z-30">
                  <ul className="flex flex-col text-gray-700">
                    {isAdmin() ? (
                      <>
                        <li
                          className="py-2 px-8 hover:bg-gray-400 cursor-pointer"
                          onClick={() => navigate("/admin/dashboard")}
                        >
                          Admin Panel
                        </li>
                        <li
                          className="py-2 px-8 hover:bg-gray-400 cursor-pointer"
                          onClick={logout}
                        >
                          Logout
                        </li>
                      </>
                    ) : (
                      <>
                        <li
                          className="py-2 px-8 hover:bg-gray-400 cursor-pointer"
                          onClick={() => navigate("/user/orders")}
                        >
                          My Orders
                        </li>
                        <li
                          className="py-2 px-8 hover:bg-gray-400 cursor-pointer"
                          onClick={() => navigate("/user/profile")}
                        >
                          My Account
                        </li>
                        <li
                          className="py-2 px-8 hover:bg-gray-400 cursor-pointer"
                          onClick={() => navigate("/user/setting")}
                        >
                          Settings
                        </li>
                        <li
                          className="py-2 px-8 hover:bg-gray-400 cursor-pointer"
                          onClick={logout}
                        >
                          Logout
                        </li>
                      </>
                    )}
                  </ul>
                </div>
              </div>
            ) : (
              <div
                onClick={loginModalOpen}
                className="cursor-pointer hover:bg-gray-200 px-2 py-2 rounded-lg"
                title="Login"
              >
                <svg
                  className="w-8 h-8 text-gray-600 hover:text-gray-800"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M11 16l-4-4m0 0l4-4m-4 4h14m-5 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1"
                  />
                </svg>
              </div>
            )}
          </div>
        </div>
      </nav>
    </Fragment>
  );
};

export default Navber;
