import React, { Fragment, useContext, useState, useEffect } from "react";
import { ProductContext } from "./index";
import { createProduct, getAllProduct } from "./FetchApi";
import { getAllCategory } from "../categories/FetchApi";

const AddProductDetail = ({ categories }) => {
  const { data, dispatch } = useContext(ProductContext);

  const alert = (msg, type) => (
    <div className={`bg-${type}-200 py-2 px-4 w-full`}>{msg}</div>
  );

  const [fData, setFdata] = useState({
    pName: "",
    pDescription: "",
    pStatus: "Active",
    pImage: null,
    pCategory: "",
    pPrice: "",
    pOldPrice: "",
    pOffer: 0,
    pQuantity: "",
    pSeasonal: false,
    pAnimated: false,
    success: false,
    error: false,
  });

  const fetchData = async () => {
    const responseData = await getAllProduct();
    if (responseData && responseData.Products) {
      dispatch({
        type: "fetchProductsAndChangeState",
        payload: responseData.Products,
      });
    }
  };

  const submitForm = async (e) => {
    e.preventDefault();
    e.target.reset();

    if (!fData.pImage || fData.pImage.length < 2) {
      setFdata({ ...fData, error: "Please upload at least 2 images" });
      setTimeout(() => {
        setFdata({ ...fData, error: false });
      }, 2000);
      return;
    }

    try {
      const responseData = await createProduct(fData);
      if (responseData.success) {
        fetchData();
        setFdata({
          pName: "",
          pDescription: "",
          pStatus: "Active",
          pImage: null,
          pCategory: "",
          pPrice: "",
          pOldPrice: "",
          pOffer: 0,
          pQuantity: "",
          pSeasonal: false,
          pAnimated: false,
          success: responseData.success,
          error: false,
        });
        setTimeout(() => {
          setFdata((prev) => ({ ...prev, success: false }));
        }, 2000);
      } else if (responseData.error) {
        setFdata({ ...fData, success: false, error: responseData.error });
        setTimeout(() => {
          setFdata((prev) => ({ ...prev, error: false }));
        }, 2000);
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Fragment>
      {/* Overlay */}
      <div
        onClick={() => dispatch({ type: "addProductModal", payload: false })}
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed top-0 left-0 z-30 w-full h-full bg-black opacity-50`}
      />
      {/* Modal */}
      <div
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed inset-0 z-40 flex items-center justify-center`}
      >
        <div className="bg-white w-11/12 md:w-3/5 p-6 rounded-lg shadow-lg space-y-4">
          <div className="flex justify-between items-center">
            <h2 className="text-2xl font-bold">Add Product</h2>
            <button
              onClick={() => dispatch({ type: "addProductModal", payload: false })}
              className="text-xl px-3 py-1 rounded-full bg-black text-white"
            >
              Ã—
            </button>
          </div>

          {fData.error && alert(fData.error, "red")}
          {fData.success && alert(fData.success, "green")}

          <form onSubmit={submitForm} className="space-y-4">
            <div className="flex gap-4">
              <input
                type="text"
                placeholder="Product Name"
                className="w-1/2 border px-4 py-2"
                value={fData.pName}
                onChange={(e) => setFdata({ ...fData, pName: e.target.value })}
              />
              <input
                type="number"
                placeholder="Price"
                className="w-1/2 border px-4 py-2"
                value={fData.pPrice}
                onChange={(e) => setFdata({ ...fData, pPrice: e.target.value })}
              />
            </div>

            <input
              type="number"
              placeholder="Old Price"
              className="w-full border px-4 py-2"
              value={fData.pOldPrice}
              onChange={(e) => setFdata({ ...fData, pOldPrice: e.target.value })}
            />

            <textarea
              placeholder="Description"
              className="w-full border px-4 py-2"
              rows={3}
              value={fData.pDescription}
              onChange={(e) => setFdata({ ...fData, pDescription: e.target.value })}
            />

            <input
              type="file"
              className="w-full border px-4 py-2"
              multiple
              onChange={(e) => setFdata({ ...fData, pImage: [...e.target.files] })}
            />

            <div className="flex gap-4">
              <select
                className="w-1/2 border px-4 py-2"
                value={fData.pStatus}
                onChange={(e) => setFdata({ ...fData, pStatus: e.target.value })}
              >
                <option value="Active">Active</option>
                <option value="Disabled">Disabled</option>
              </select>

              <select
                className="w-1/2 border px-4 py-2"
                value={fData.pCategory}
                onChange={(e) => setFdata({ ...fData, pCategory: e.target.value })}
              >
                <option value="">Select Category</option>
                {categories.map((cat) => (
                  <option value={cat._id} key={cat._id}>
                    {cat.cName}
                  </option>
                ))}
              </select>
            </div>

            <div className="flex gap-4">
              <input
                type="number"
                placeholder="Quantity"
                className="w-1/2 border px-4 py-2"
                value={fData.pQuantity}
                onChange={(e) => setFdata({ ...fData, pQuantity: e.target.value })}
              />
              <input
                type="number"
                placeholder="Offer %"
                className="w-1/2 border px-4 py-2"
                value={fData.pOffer}
                onChange={(e) => setFdata({ ...fData, pOffer: e.target.value })}
              />
            </div>

            <div className="flex items-center space-x-6">
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={fData.pSeasonal}
                  onChange={(e) => setFdata({ ...fData, pSeasonal: e.target.checked })}
                />
                <span>Seasonal</span>
              </label>

              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={fData.pAnimated}
                  onChange={(e) => setFdata({ ...fData, pAnimated: e.target.checked })}
                />
                <span>Animated</span>
              </label>
            </div>

            <button
              type="submit"
              className="w-full bg-black text-white py-2 rounded-full"
            >
              Create Product
            </button>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

const AddProductModal = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchCategoryData = async () => {
      const res = await getAllCategory();
      if (res.Categories) {
        setCategories(res.Categories);
      }
    };
    fetchCategoryData();
  }, []);

  return <AddProductDetail categories={categories} />;
};

export default AddProductModal;
