import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

import {
  clearAllMessageErrors,
  deleteMessage,
  getAllMessages,
  resetMessagesSlice,
} from "./messageSlice";

const Messages = () => {
  const navigateTo = useNavigate();
  const dispatch = useDispatch();

  const { messages, loading, error, message } = useSelector(
    (state) => state.messages
  );

  const [messageId, setMessageId] = useState("");

  const handleReturnToDashboard = () => {
    navigateTo("/");
  };

  const handleMessageDelete = (id) => {
    setMessageId(id);
    dispatch(deleteMessage(id));
  };

  useEffect(() => {
    dispatch(getAllMessages());
  }, [dispatch]);

  useEffect(() => {
    if (error) {
      toast.error(error);
      dispatch(clearAllMessageErrors());
    }
    if (message) {
      toast.success(message);
      dispatch(resetMessagesSlice());
      dispatch(getAllMessages());
    }
  }, [dispatch, error, message]);

  return (
    <div className="min-h-screen px-4 py-8 bg-gray-100">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">Messages</h2>
        <button
          onClick={handleReturnToDashboard}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
        >
          Return to Dashboard
        </button>
      </div>

      <div className="grid sm:grid-cols-2 gap-6">
        {messages && messages.length > 0 ? (
          messages.map((element) => (
            <div
              key={element._id}
              className="bg-white p-4 rounded-lg shadow-md space-y-2"
            >
              <p className="text-gray-800">
                <strong>Sender Name:</strong> {element.senderName}
              </p>
              <p className="text-gray-800">
                <strong>Subject:</strong> {element.subject}
              </p>
              <p className="text-gray-800">
                <strong>Message:</strong> {element.message}
              </p>

              <div className="flex justify-end mt-4">
                {loading && messageId === element._id ? (
                  <button
                    disabled
                    className="bg-red-400 text-white px-4 py-2 rounded flex items-center gap-2"
                  >
                    <svg
                      className="animate-spin h-4 w-4 text-white"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="none"
                      viewBox="0 0 24 24"
                    >
                      <circle
                        className="opacity-25"
                        cx="12"
                        cy="12"
                        r="10"
                        stroke="currentColor"
                        strokeWidth="4"
                      ></circle>
                      <path
                        className="opacity-75"
                        fill="currentColor"
                        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"
                      ></path>
                    </svg>
                    Deleting
                  </button>
                ) : (
                  <button
                    onClick={() => handleMessageDelete(element._id)}
                    className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                  >
                    Delete
                  </button>
                )}
              </div>
            </div>
          ))
        ) : (
          <div className="col-span-2 text-center text-xl text-gray-600">
            No Messages Found!
          </div>
        )}
      </div>
    </div>
  );
};

export default Messages;
