import React, { Fragment, useContext, useState, useEffect } from "react";
import { ProductContext } from "./index";
import { createProduct, getAllProduct } from "./FetchApi";
import { getAllCategory } from "../categories/FetchApi";

const AddProductDetail = ({ categories }) => {
  const { data, dispatch } = useContext(ProductContext);

  const alert = (msg, type) => (
    <div className={`bg-${type}-200 py-2 px-4 w-full`}>{msg}</div>
  );

  const [fData, setFdata] = useState({
    pName: "",
    pDescription: "",
    pStatus: "Active",
    pImage: null,
    pCategory: "",
    oldPrice: "",
    newPrice: "",
    pOffer: 0,
    pQuantity: "",
    topCollection: false,
    solarPerfume: false,
    discount: false,
    seasonalProduct: false,
    animation: false,
    success: false,
    error: false,
  });

  const fetchData = async () => {
    const responseData = await getAllProduct();
    if (responseData && responseData.Products) {
      dispatch({
        type: "fetchProductsAndChangeState",
        payload: responseData.Products,
      });
    }
  };

  const submitForm = async (e) => {
    e.preventDefault();

    if (!fData.pImage || fData.pImage.length !== 2) {
      setFdata({ ...fData, error: "Please upload exactly 2 images" });
      setTimeout(() => setFdata((prev) => ({ ...prev, error: false })), 2000);
      return;
    }

    if (
      !fData.pName ||
      !fData.pDescription ||
      !fData.oldPrice ||
      !fData.newPrice ||
      !fData.pQuantity ||
      !fData.pCategory
    ) {
      setFdata({ ...fData, error: "Please fill all required fields" });
      setTimeout(() => setFdata((prev) => ({ ...prev, error: false })), 2000);
      return;
    }

    const formData = new FormData();
    formData.append("pName", fData.pName);
    formData.append("pDescription", fData.pDescription);
    formData.append("pStatus", fData.pStatus);
    formData.append("pCategory", fData.pCategory);
    formData.append("oldPrice", fData.oldPrice);
    formData.append("newPrice", fData.newPrice);
    formData.append("pQuantity", fData.pQuantity);
    formData.append("pOffer", fData.pOffer);
    formData.append("topCollection", fData.topCollection);
    formData.append("solarPerfume", fData.solarPerfume);
    formData.append("discount", fData.discount);
    formData.append("seasonalProduct", fData.seasonalProduct);
    formData.append("animation", fData.animation);

    fData.pImage.forEach((img) => {
      formData.append("pImage", img);
    });

    try {
      const response = await createProduct(formData);
      if (response.success) {
        fetchData();
        setFdata({
          pName: "",
          pDescription: "",
          pStatus: "Active",
          pImage: null,
          pCategory: "",
          oldPrice: "",
          newPrice: "",
          pOffer: 0,
          pQuantity: "",
          topCollection: false,
          solarPerfume: false,
          discount: false,
          seasonalProduct: false,
          animation: false,
          success: response.success,
          error: false,
        });
        document.getElementById("product-form").reset();
        setTimeout(() => setFdata((prev) => ({ ...prev, success: false })), 2000);
      } else {
        setFdata({ ...fData, error: response.error });
        setTimeout(() => setFdata((prev) => ({ ...prev, error: false })), 2000);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Fragment>
      {/* Modal Overlay */}
      <div
        onClick={() => dispatch({ type: "addProductModal", payload: false })}
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed top-0 left-0 z-30 w-full h-full bg-black opacity-50`}
      />
      {/* Modal Body */}
      <div
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed inset-0 flex items-center z-30 justify-center overflow-auto`}
      >
        <div className="mt-32 md:mt-0 bg-white w-11/12 md:w-3/6 shadow-lg flex flex-col items-center px-4 py-4 md:px-8">
          <div className="flex items-center justify-between w-full pt-4">
            <span className="text-2xl font-semibold">Add Product</span>
            <span
              style={{ background: "#303031" }}
              onClick={() => dispatch({ type: "addProductModal", payload: false })}
              className="cursor-pointer text-white py-2 px-2 rounded-full"
            >
              ✖
            </span>
          </div>

          {fData.error && alert(fData.error, "red")}
          {fData.success && alert("Product created successfully!", "green")}

          <form
            className="w-full"
            onSubmit={submitForm}
            id="product-form"
            encType="multipart/form-data"
          >
            {/* Name & Prices */}
            <div className="flex space-x-2 py-2">
              <div className="w-1/2">
                <label>Product Name *</label>
                <input
                  type="text"
                  className="w-full border px-4 py-2"
                  value={fData.pName}
                  onChange={(e) =>
                    setFdata({ ...fData, pName: e.target.value, error: false })
                  }
                />
              </div>
              <div className="w-1/2">
                <label>Old Price *</label>
                <input
                  type="number"
                  className="w-full border px-4 py-2"
                  value={fData.oldPrice}
                  onChange={(e) =>
                    setFdata({ ...fData, oldPrice: e.target.value, error: false })
                  }
                />
              </div>
            </div>

            <div className="flex space-x-2 py-2">
              <div className="w-1/2">
                <label>New Price *</label>
                <input
                  type="number"
                  className="w-full border px-4 py-2"
                  value={fData.newPrice}
                  onChange={(e) =>
                    setFdata({ ...fData, newPrice: e.target.value, error: false })
                  }
                />
              </div>
              <div className="w-1/2">
                <label>Quantity *</label>
                <input
                  type="number"
                  className="w-full border px-4 py-2"
                  value={fData.pQuantity}
                  onChange={(e) =>
                    setFdata({ ...fData, pQuantity: e.target.value, error: false })
                  }
                />
              </div>
            </div>

            {/* Description */}
            <div className="py-2">
              <label>Description *</label>
              <textarea
                rows="2"
                className="w-full border px-4 py-2"
                value={fData.pDescription}
                onChange={(e) =>
                  setFdata({ ...fData, pDescription: e.target.value, error: false })
                }
              />
            </div>

            {/* Images */}
            <div className="py-2">
              <label>Product Images *</label>
              <input
                type="file"
                multiple
                accept=".jpg,.jpeg,.png"
                className="w-full border px-4 py-2"
                onChange={(e) =>
                  setFdata({ ...fData, pImage: [...e.target.files], error: false })
                }
              />
              <span className="text-sm text-gray-500">Upload exactly 2 images</span>
            </div>

            {/* Status & Category */}
            <div className="flex space-x-2 py-2">
              <div className="w-1/2">
                <label>Status *</label>
                <select
                  className="w-full border px-4 py-2"
                  value={fData.pStatus}
                  onChange={(e) =>
                    setFdata({ ...fData, pStatus: e.target.value, error: false })
                  }
                >
                  <option value="Active">Active</option>
                  <option value="Disabled">Disabled</option>
                </select>
              </div>
              <div className="w-1/2">
                <label>Category *</label>
                <select
                  className="w-full border px-4 py-2"
                  value={fData.pCategory}
                  onChange={(e) =>
                    setFdata({ ...fData, pCategory: e.target.value, error: false })
                  }
                >
                  <option value="">Select Category</option>
                  {categories.map((cat) => (
                    <option key={cat._id} value={cat._id}>
                      {cat.cName}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            {/* Checkboxes */}
            <div className="grid grid-cols-2 md:grid-cols-3 gap-3 py-2">
              {[
                ["Top Collection", "topCollection"],
                ["Solar Perfume", "solarPerfume"],
                ["Discount", "discount"],
                ["Seasonal Product", "seasonalProduct"],
                ["Animation", "animation"],
              ].map(([label, key]) => (
                <label key={key} className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={fData[key]}
                    onChange={(e) =>
                      setFdata({ ...fData, [key]: e.target.checked })
                    }
                  />
                  <span>{label}</span>
                </label>
              ))}
            </div>

            {/* Submit */}
            <div className="mt-4">
              <button
                type="submit"
                className="w-full bg-gray-800 text-white py-2 rounded-full"
              >
                Create Product
              </button>
            </div>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

const AddProductModal = () => {
  const [allCat, setAllCat] = useState([]);

  const fetchCategoryData = async () => {
    const response = await getAllCategory();
    if (response.Categories) {
      setAllCat(response.Categories);
    }
  };

  useEffect(() => {
    fetchCategoryData();
  }, []);

  return (
    <Fragment>
      <AddProductDetail categories={allCat} />
    </Fragment>
  );
};

export default AddProductModal;
